package com.env.web.vo;

/**
 * 地图监测上方信息条
 * @author lz
 *
 */
public class MapHeaderVo {
	/**
	 * 信息项
	 */
	private String name;
	/**
	 * 信息值
	 */
	private String value;
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public String getValue() {
		return value;
	}
	public void setValue(String value) {
		this.value = value;
	}
	
}
